import classNames from 'classnames';
import VuePerfectScrollbar from 'vue-perfect-scrollbar';

const Scrollbar = {
  components: {
    VuePerfectScrollbar
  },
  props: {
    wheelSpeed: {
      type: Number,
      default: 1
    },
    wheelPropagation: {
      type: Boolean,
      default: false
    },
    swipeEasing: {
      type: Boolean,
      default: true
    },
    minScrollbarLength: {
      type: Number,
      default: null
    },
    maxScrollbarLength: {
      type: Number,
      default: null
    },
    scrollingThreshold: {
      type: Number,
      default: 1000
    },
    useBothWheelAxes: {
      type: Boolean,
      default: false
    },
    suppressScrollX: {
      type: Boolean,
      default: false
    },
    suppressScrollY: {
      type: Boolean,
      default: false
    },
    scrollXMarginOffset: {
      type: Number,
      default: 0
    },
    scrollYMarginOffset: {
      type: Number,
      default: 0
    },
    scrollClass: {
      type: String
    },
    scrollStyle: {
      type: String
    },
    width: {
      type: String,
      default: '100%'
    },
    height: {
      type: String,
      default: '100%'
    }
  },
  data() {
    return {
      name: 'Scrollbar',
      yRail: null,
      settings: {
        wheelSpeed: this.wheelSpeed,
        wheelPropagation: this.wheelPropagation,
        swipeEasing: this.swipeEasing,
        minScrollbarLength: this.minScrollbarLength,
        maxScrollbarLength: this.maxScrollbarLength,
        scrollingThreshold: this.scrollingThreshold,
        useBothWheelAxes: this.useBothWheelAxes,
        suppressScrollX: this.suppressScrollX,
        suppressScrollY: this.suppressScrollY,
        scrollXMarginOffset: this.scrollXMarginOffset,
        scrollYMarginOffset: this.scrollYMarginOffset
      }
    };
  },
  computed: {
    className() {
      return classNames(
        this.scrollClass
      );
    },
    scrollStyles() {
      return this.scrollStyle;
    }
  },
  methods: {
    hover() {
      this.yRail.style.opacity = 1;
    },
    hoverOut() {
      this.yRail.style.opacity = 0;
    },
    setStyle(prop, value){
      this.$el.style[prop] = value;
    }
  },
  mounted() {
    this.yRail = document.querySelector('.ps__scrollbar-y-rail');
    this.yRail.classList.remove('ps__scrollbar-y-rail');
    this.yRail.classList.add('ps-y-rail');
    this.setStyle('width', this.width);
    this.setStyle('height', this.height);
  }
};

export default Scrollbar;
export { Scrollbar as mdbScrollbar };
